    def upload_employee_data(self, input_from_paycom: list):
        for each in input_from_paycom:
            name = each['name']
            if each['Status'] == 'Active':
                status = 'Working'
            else:
                status = ""
            for key in each.keys():
                if 'Phone Number' in key:
                    phone = re.sub('[\s()-]', "", each[key])
            bti_email = each['Email - Work']
            personal_email = each['Email - Personal']
            temp_date_list = each['Hire Date'].split('/')
            start_date = temp_date_list[2] + "-" + temp_date_list[0] + "-" + temp_date_list[1]
            title = each['position']

            query = 'mutation ($myItemName: String!, $columnVals: JSON!) { create_item (board_id:' + self.PEOPLE_BOARD_ID + 'item_name:$myItemName, column_values:$columnVals) { id } }'

            vars = {
                'myItemName': name,
                'columnVals': json.dumps({
                    'status': {'label': status},
                    'phone4': {'phone': phone, 'countryShortName': 'US'},
                    'email1': {'email': bti_email, 'text': bti_email},
                    'email17': {'email': personal_email, 'text': personal_email},
                    'date4': {'date': start_date},
                    'text1': {'text': title}
                })
            }

            data = {'query': query, 'variables': vars}

            r = requests.post(url=self.API_URL, json=data, headers=self.headers)
            print(r.json)
            print(r.text)

    def upload_time_off_data(self, input_from_paycom: list):
        for each_entry in input_from_paycom:
            name = each_entry['single_column_data0']
            date = each_entry['Date']
            time_off_type = 'Unknown'
            time_off_hours = ''
            for each_key in each_entry.keys():
                if each_entry[each_key].endswith('hours'):
                    time_off_type = each_key
                    time_off_hours = each_entry[each_key].split(' ')[0]
            reason = ''
            if 'Reason' in each_entry:
                reason = each_entry['Reason']

            my_item_name = name + ' O_' + time_off_hours

            query = 'mutation ($myItemName: String!, $columnVals: JSON!) { create_item (board_id:' + self.TIMESHEET_BOARD_ID + 'item_name:$myItemName, column_values:$columnVals) { id } }'

            vars = {
                'myItemName': my_item_name,
                'columnVals': json.dumps({
                    'date5': {'date': date},
                    'status9': {'label': time_off_type},
                    'numbers4': time_off_hours,
                    'text4': reason
                })
            }

            data = {'query': query, 'variables': vars}

            r = requests.post(url=self.API_URL, json=data, headers=self.headers)
            print(r.json)
            print(r.text)

    def get_all_boards(self):
        query = '{ boards {name id} }'
        data = {'query': query}

        r = requests.post(url=self.API_URL, json=data, headers=self.headers)
        print(r.json)
        print(r.text)

    def get_single_board_data(self, board_id):
        query2 = '{boards(ids: ' + board_id + ') { name id description items { name id column_values{title id type text } } } }'
        data = {'query': query2}

        r = requests.post(url=self.API_URL, json=data, headers=self.headers)
        print(r.json)
        print(r.text)


